mapper.insert:
INSERT INTO orders( order_code, company_code, customer_code, receive_or_place, receive_order_type, order_date, order_empid, delivery_plan, order_status, due_date, memo, reg_date, reg_user, update_date, update_user
) VALUES(
'ORDER' || order_code_SEQ.NEXTVAL, #{companyCode, jdbcType=VARCHAR}, #{customerCode, jdbcType=VARCHAR}, '수주', #{receiveOrderType, jdbcType=VARCHAR}, #{orderDate, jdbcType=VARCHAR}, #{orderEmpid, jdbcType=VARCHAR}, #{deliveryPlan, jdbcType=VARCHAR}, '저장', #{dueDate, jdbcType=VARCHAR}, #{memo, jdbcType=VARCHAR}, SYSDATE, #{regUser, jdbcType=VARCHAR}, SYSDATE, #{updateUser, jdbcType=VARCHAR}
)

mapper.update:
UPDATE	orders_detail
SET		quantity = #{quantity},
		price = #{price},
		amount = #{amount}
WHERE	order_detail_code = #{orderDetailCode}

UPDATE	orders_detail
SET		item_stock_unit = #{itemStockUnit}
WHERE	order_detail_code = #{orderDetailCode}

UPDATE	orders_detail
SET		memo = #{memo}
WHERE	order_detail_code = #{orderDetailCode}

UPDATE	orders_detail
SET		end_yn = #{endYn}
WHERE	order_detail_code = #{orderDetailCode}

mapper.select:

	SELECT	order_status
	FROM	orders
	WHERE	order_code = #{orderCode}

mapper.update:
