mapper.select:
SELECT s.out_type, s.out_code, s.delete_status, s.customer_code, c.company_name, s.out_date, s.order_code, s.reg_user, s.reg_date, s.update_user , s.update_date, COALESCE(s.memo, ' ') AS memo FROM st_out s LEFT JOIN company c ON s.customer_code = c.company_code
AND s.customer_code = #{customerCode}
ORDER BY LENGTH(s.out_code),s.out_code ASC

mapper.update:
UPDATE st_out 
SET out_type = '확정'
WHERE out_code = #{outCode}

UPDATE st_out 
SET out_type = '저장'
WHERE out_code = #{outCode}

UPDATE st_out
SET delete_status = 'Y'
WHERE out_code = #{outCode}

UPDATE st_out_detail 
SET out_quantity = #{outQuantity},
	memo = #{memo2}
WHERE out_code = #{outCode} and sorder = #{sorder}

UPDATE st_out_detail
SET delete_status = 'Y'
WHERE out_code = #{outCode}
AND sorder = #{sorder}

mapper.select:
{
	CALL STOUT_DETAIL_INSERT(
		#{outCode, 			mode=IN,  jdbcType=VARCHAR},
		#{whCode, 			mode=IN,  jdbcType=VARCHAR},
		#{itemCode, 		mode=IN,  jdbcType=VARCHAR},
		#{outQuantity, 		mode=IN,  jdbcType=NUMERIC},
		#{memo2, 			mode=IN,  jdbcType=VARCHAR}
	)
}

SELECT s.delete_status, sd.delete_status, s.out_type, s.customer_code, c.company_name, s.order_code, s.out_code, s.out_date, s.reg_user, s.reg_date, i.item_name, i.item_code, sd.out_quantity, i.sales_price, (i.sales_price * sd.out_quantity) AS totalPrice, sd.memo as memo2 FROM st_out_detail sd LEFT JOIN st_out s ON sd.out_code = s.out_code LEFT JOIN item i ON sd.item_code = i.item_code LEFT JOIN company c ON s.customer_code = c.company_code WHERE s.delete_status = 'N' and sd.delete_status = 'N'
and s.customer_code = #{customerCode}
ORDER BY LENGTH(c.company_code),c.company_code ASC, sd.sorder ASC

mapper.update:
UPDATE wh_stock_detail
SET good_quantity = good_quantity - #{outQuantity}
WHERE item_code = #{itemCode} and wh_code = #{whCode}

UPDATE wh_stock_detail
SET good_quantity = good_quantity + #{outQuantity}
WHERE item_code = #{itemCode} and wh_code = #{whCode}
